import logo from './logo.svg';
import './App.css';
import './questions'
import {useState} from "react";

const QUESTION = [
  {
    id:1,
    question1: "AAAAAAAAAAA",
    answer1: "A",
    answer2: "B",
    answer3: "C",
    answer4: "D",
    correct: "A"
  },
  {
    id:2,
    question1: "CCCCCCCCCC",
    answer1: "A",
    answer2: "B",
    answer3: "C",
    answer4: "D",
    correct: "C"
  },
  {
    id:3,
    question1: "DDDDDDDDDD",
    answer1: "A",
    answer2: "B",
    answer3: "C",
    answer4: "D",
    correct: "D"
  }
]
const TIME_LIMIT = 10;

function Question(){
    QUESTION.map((question)=>{
        return (
            <div>
                <h3>Q1: {question.question1}</h3>
                <ul>
                    <li>{question.answer1}</li>
                    <li>{question.answer2}</li>
                    <li>{question.answer3}</li>
                    <li>{question.answer4}</li>
                </ul>
            </div>
        )  
    })
}
function App() {
  const [question, setQuestion] = useState(QUESTION)
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [timer, setTimer] = useState(TIME_LIMIT)
  function startGame(){
    // setInterval(() => {
    //   console.log("run interval")
    //   setTimer((prevState)=>{
    //     return prevState-1});
    // }, 1000); 
    const interval = setInterval(() => {
      console.log("run interval", timer)
      setTimer((prevTimer) =>{
        if(prevTimer > 0){
          return prevTimer - 1;
        }
        else{
          clearInterval(interval);
        return 0;
        }
      });
    }, 1000);
  }
  return (
    <div className="App">
      <header className="App-header">
        <h1>{timer}</h1>
        <img src={logo} className="App-logo" alt="logo" />
        <button onClick={startGame}>start</button>
        <Questions/>
        {currentQuestion}
      </header>
    </div>
  );
}

export default App;
