function App() {
  const QUESTION = [
    {
      id:1,
      question1: "AAAAAAAAAAA",
      answer1: "A",
      answer2: "B",
      answer3: "C",
      answer4: "D",
      correct: "A"
    },
    {
      id:2,
      question1: "CCCCCCCCCC",
      answer1: "A",
      answer2: "B",
      answer3: "C",
      answer4: "D",
      correct: "C"
    },
    {
      id:3,
      question1: "DDDDDDDDDD",
      answer1: "A",
      answer2: "B",
      answer3: "C",
      answer4: "D",
      correct: "D"
    }
  ]
  const [questions, setQuestions] = useState(QUESTION);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [timer, setTimer] = useState(TIME_LIMIT)
  function startGame(){
    const interval = setInterval(() => {
      console.log("run interval", timer)
      setTimer((prevTimer) =>{
        if(prevTimer > 0){
          return prevTimer - 1;
        }
        else{
          clearInterval(interval);
        return 0;
        }
      });
    }, 1000);
  }
  const question = questions[currentQuestion];
  function nextQuestion(){
    if(currentQuestion < questions.length -1)
   setCurrentQuestion(currentQuestion+1);
  }
  return (
    <div className="App">
      <header className="App-header">
        <h1>{timer}</h1>
        <img src={logo} className="App-logo" alt="logo" />
        <button onClick={startGame}>start</button>
        <div>
//                 <h3>Q1: {question.question1}</h3>
                <ul>
                  <li>{question.answer1}</li>
                   <li>{question.answer2}</li>
                    <li>{question.answer3}</li>
                     <li>{question.answer4}</li>
                 </ul>
      </div>
        <button onClick={nextQuestion}>Next</button>
        {/* <Question/> */}
      </header>
    </div>
  );
}

export default App;
