import logo from "./logo.svg";
import "./App.css";
// import Question from './questions';
import { useState } from "react";

const TIME_LIMIT = 5;

function App() {
  const QUESTION = [
    {
      id: 1,
      question1: "what's your name?",
      answer1: "Giang",
      answer2: "Nguyen",
      answer3: "Truong",
      answer4: "Nam",
      correct: "Giang",
    },
    {
      id: 2,
      question1: "how old are you?",
      answer1: "10",
      answer2: "16",
      answer3: "18",
      answer4: "26",
      correct: "26",
    },
    {
      id: 3,
      question1: "Where are you from?",
      answer1: "Ha Noi",
      answer2: "Hai Phong",
      answer3: "Sai Gon",
      answer4: "Malaysia",
      correct: "Ha Noi",
    },
  ];
  const [questions, setQuestions] = useState(QUESTION);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [questionNumber, setQuestionNumber] = useState(1)
  const [timer, setTimer] = useState(TIME_LIMIT);
  const [trueFalse, setTrueFalse] = useState("");
  const [countTrue, setCountTrue] = useState(0);
  const [countFalse, setCountFalse] = useState(0);

  function startGame() {
    const interval = setInterval(() => {
      console.log("run interval", timer);
      setTimer((prevTimer) => {
        if (prevTimer > 0) {
          return prevTimer - 1;
        } else {
          clearInterval(interval);
          return 0;
        }
      });
    }, 1000);
  }
  const question = questions[currentQuestion];

  function nextQuestion() {
    if (currentQuestion < questions.length - 1){
      setCurrentQuestion(currentQuestion + 1);
      setQuestionNumber(questionNumber + 1);
      setTimer(5);
      setTrueFalse("");

      const interval = setInterval(() => {
        // console.log("run interval", timer);
        setTimer((prevTimer) => {
          console.log(prevTimer);
          if (prevTimer > 0) {
            return prevTimer - 1;
          } else {
            clearInterval(interval);
            return 0;
          }
        });
      }, 1000);
    } else{
     setCurrentQuestion(0);
     setQuestionNumber(1);
    }
  }

  function autoNextQuestion(){
    if(timer==0 && currentQuestion < questions.length - 1){
      setCurrentQuestion(currentQuestion + 1);
      setQuestionNumber(questionNumber + 1);
      setTimer(5);
      setTrueFalse("");
      setCountFalse(countFalse + 1);
    } 
    else if(currentQuestion == questions.length - 1){
      // setCurrentQuestion(0);
      // setQuestionNumber(1);
      // setTimer(5);
    }
  }
  autoNextQuestion();

  function chooseA(){
    if(questions[currentQuestion].answer1===questions[currentQuestion].correct){
      setTrueFalse("true");
      setCountTrue(countTrue + 1);
      // setCurrentQuestion(currentQuestion + 1);
      // setQuestionNumber(questionNumber + 1);
      // setTimer(5);
      // setTrueFalse("")
    }
    else setTrueFalse("false");
    setCountFalse(countFalse + 1);
  }
  function chooseB(){
    if(questions[currentQuestion].answer2===questions[currentQuestion].correct){
      setTrueFalse("true");
      setCountTrue(countTrue + 1);
      // setCurrentQuestion(currentQuestion + 1);
      // setQuestionNumber(questionNumber + 1);
      // setTimer(5);
      // setTrueFalse("")
    }
    else setTrueFalse("false");
    setCountFalse(countFalse + 1);
  }
  function chooseC(){
    if(questions[currentQuestion].answer3===questions[currentQuestion].correct){
      setTrueFalse("true");
      setCountTrue(countTrue + 1);
      // setCurrentQuestion(currentQuestion + 1);
      // setQuestionNumber(questionNumber + 1);
      // setTimer(5);
      // setTrueFalse("")
    }
    else setTrueFalse("false");
    setCountFalse(countFalse + 1);
  }
  function chooseD(){
    if(questions[currentQuestion].answer4===questions[currentQuestion].correct){
      setTrueFalse("true");
      setCountTrue(countTrue + 1);
      // setCurrentQuestion(currentQuestion + 1);
      // setQuestionNumber(questionNumber + 1);
      // setTimer(5);
      // setTrueFalse("")
    }
    else setTrueFalse("false");
    setCountFalse(countFalse + 1);
  }

  function playAgain(){
    setCurrentQuestion(0);
    setQuestionNumber(1);
    setCountTrue()
  }

  if(currentQuestion == questions.length - 1){
    return (
      <div className="App">
        <header className="App-header">
          <h1>Finish</h1>
          <img src={logo} className="App-logo" alt="logo" />
          <button onClick={playAgain}>Play again</button>
          <div>
            <h3>True: {countTrue}; False: {countFalse};</h3>
          </div>
        </header>
      </div>
    )
  } else return (
    <div className="App">
      <header className="App-header">
        <h1>{timer}</h1>
        <img src={logo} className="App-logo" alt="logo" />
        <button onClick={startGame}>start</button>
        <div>
          <h3>T/F: {trueFalse}</h3>
          <h3>Question {questionNumber}: {question.question1}</h3>
          <ol>
            <li value = "1" onClick={chooseA}>{question.answer1}</li>
            <li onClick={chooseB}>{question.answer2}</li>
            <li onClick={chooseC}>{question.answer3}</li>
            <li onClick={chooseD}>{question.answer4}</li>
          </ol>
        </div>
        <button id="next" onClick={nextQuestion}>Next</button>
        {/* <Question/> */}
      </header>
    </div>
  );
}

export default App;
