import logo from "./logo.svg";
import "./App.css";
// import Question from './questions';
import { useState } from "react";

const TIME_LIMIT = 5;

function App() {
  const QUESTION = [
    {
      id: 1,
      question1: "How many days are in a week?",
      answer1: "2",
      answer2: "4",
      answer3: "7",
      answer4: "10",
      correct: "7",
    },
    {
      id: 2,
      question1: "how old are you?",
      answer1: "10",
      answer2: "16",
      answer3: "18",
      answer4: "26",
      correct: "26",
    },
    {
      id: 3,
      question1: "Where are you from?",
      answer1: "Ha Noi",
      answer2: "Hai Phong",
      answer3: "Sai Gon",
      answer4: "Malaysia",
      correct: "Ha Noi",
    },
  ];
  const [questions, setQuestions] = useState(QUESTION);
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);
  const [questionNumber, setQuestionNumber] = useState(1)
  const [timer, setTimer] = useState(TIME_LIMIT);
  const [trueFalse, setTrueFalse] = useState("");
  const [countTrue, setCountTrue] = useState(0);
  const [countFalse, setCountFalse] = useState(0);

  function startGame() {
    const interval = setInterval(() => {
      console.log("run interval", timer);
      setTimer((prevTimer) => {
        if (prevTimer > 0) {
          return prevTimer - 1;
        } else {
          clearInterval(interval);
          return 0;
        }
      });
    }, 1000);
  }
  const question = questions[currentQuestionIdx];

  function nextQuestion() {
    if (currentQuestionIdx < questions.length){
      setCurrentQuestionIdx(currentQuestionIdx + 1);
      setQuestionNumber(questionNumber + 1);
      setTimer(5);
      setTrueFalse("");

      const interval = setInterval(() => {
        // console.log("run interval", timer);
        setTimer((prevTimer) => {
          console.log(prevTimer);
          if (prevTimer > 0) {
            return prevTimer - 1;
          } else {
            clearInterval(interval);
            return 0;
          }
        });
      }, 1000);
    } 
    // else{
    //  setCurrentQuestionIdx(0);
    //  setQuestionNumber(1);
    // }
  }

  function autoNextQuestion(){
    if(timer==0 && currentQuestionIdx < questions.length){
      setCurrentQuestionIdx(currentQuestionIdx + 1);
      setQuestionNumber(questionNumber + 1);
      setTimer(5);
      setTrueFalse("");
      setCountFalse(countFalse + 1);
    } 
    else if(currentQuestionIdx == questions.length - 1){
      // setCurrentQuestionIdx(0);
      // setQuestionNumber(1);
      // setTimer(5);
    }
  }
  autoNextQuestion();

  function chooseA(){
    if(questions[currentQuestionIdx].answer1===questions[currentQuestionIdx].correct){
      setTrueFalse("true");
      setCountTrue(countTrue + 1);
      setCountFalse(countFalse - 1);
      // setCurrentQuestionIdx(currentQuestionIdx + 1);
      // setQuestionNumber(questionNumber + 1);
      // setTimer(5);
      // setTrueFalse("")
    }
    else {setTrueFalse("false");
          setCountFalse(countFalse);
          }
  }
  function chooseB(){
    if(questions[currentQuestionIdx].answer2===questions[currentQuestionIdx].correct){
      setTrueFalse("true");
      setCountTrue(countTrue + 1);
      setCountFalse(countFalse - 1);
      // setCurrentQuestionIdx(currentQuestionIdx + 1);
      // setQuestionNumber(questionNumber + 1);
      // setTimer(5);
      // setTrueFalse("")
    }
    else{
      setTrueFalse("false");
      setCountFalse(countFalse);
    } 
  }
  function chooseC(){
    if(questions[currentQuestionIdx].answer3===questions[currentQuestionIdx].correct){
      setTrueFalse("true");
      setCountTrue(countTrue + 1);
      setCountFalse(countFalse - 1);
      // setCurrentQuestionIdx(currentQuestionIdx + 1);
      // setQuestionNumber(questionNumber + 1);
      // setTimer(5);
      // setTrueFalse("")
    }
    else {
      setTrueFalse("false");
      setCountFalse(countFalse);
    }
  }
  function chooseD(){
    if(questions[currentQuestionIdx].answer4===questions[currentQuestionIdx].correct){
      setTrueFalse("true");
      setCountTrue(countTrue + 1);
      setCountFalse(countFalse - 1);
      // setCurrentQuestionIdx(currentQuestionIdx + 1);
      // setQuestionNumber(questionNumber + 1);
      // setTimer(5);
      // setTrueFalse("")
    }
    else{
      setTrueFalse("false");
      setCountFalse(countFalse);
    } 
  }

  function playAgain(){
    setCurrentQuestionIdx(0);
    setQuestionNumber(1);
    setTimer(5)
    setCountTrue(0);
    setCountFalse(0);
  }

  if(currentQuestionIdx == questions.length){
    return (
      <div className="App">
        <header className="App-header">
          <h1>Finish</h1>
          <img src={logo} className="App-logo" alt="logo" />
          <button onClick={playAgain}>Play again</button>
          <div>
            <h3>True: {countTrue}; False: {countFalse};</h3>
          </div>
        </header>
      </div>
    )
  } else return (
    <div className="App">
      <header className="App-header">
        <h1>{timer}</h1>
        <img src={logo} className="App-logo" alt="logo" />
        <button onClick={startGame}>start</button>
        <div>
          <h3>T/F: {trueFalse}</h3>
          <h3>Question {questionNumber}: {question.question1}</h3>
          <ol>
            <li className="answer" value = "1" onClick={chooseA}>{question.answer1}</li>
            <li className="answer"onClick={chooseB}>{question.answer2}</li>
            <li onClick={chooseC}>{question.answer3}</li>
            <li onClick={chooseD}>{question.answer4}</li>
          </ol>
        </div>
        <button id="next" onClick={nextQuestion}>Next</button>
        {/* <Question/> */}
      </header>
    </div>
  );
}

export default App;
